mapper.select:
        SELECT

        from `JY_EXAM_INFO` e
        WHERE 1 = 1

				AND exam_name LIKE CONCAT(CONCAT('%',#{examName}),'%')


				AND exam_start_date = #{examStartDate}


				AND exam_end_date = #{examEndDate}


				AND classroom LIKE CONCAT(CONCAT('%',#{classroom}),'%')


				AND subject LIKE CONCAT(CONCAT('%',#{subject}),'%')


				AND exam_status = #{examStatus}


				AND org_id = #{orgId}


				AND remark = #{remark}

		ORDER BY create_date DESC

        SELECT

        from `JY_EXAM_INFO` e
        WHERE 1 = 1

				AND exam_name LIKE CONCAT(CONCAT('%',#{examName}),'%')


				AND exam_start_date = #{examStartDate}


				AND exam_end_date <= #{examEndDate}


				AND classroom LIKE CONCAT(CONCAT('%',#{classroom}),'%')


				AND subject LIKE CONCAT(CONCAT('%',#{subject}),'%')


				AND exam_status = #{examStatus}


				AND org_id = #{orgId}


				AND remark = #{remark}


			ORDER BY  ${sort}

				${sortType}



			LIMIT ${type}

        SELECT
		e.exam_name,
		e.exam_start_date,
		e.exam_end_date,
		e.classroom,
		e.subject,
		e.exam_status,
		e.org_id,
		e.remark,
		e.create_user,
		e.create_date,
		e.update_user,
		e.update_date,
		e.id
        from `JY_EXAM_STUDENT_RELATION` es
		LEFT JOIN `JY_EXAM_INFO` e on es.exam_id = e.id
        WHERE 1 = 1

				AND es.exam_id = #{examId}


				AND es.student_id = #{studentId}


				AND es.exam_stu_no = #{examStuNo}


				AND es.status = #{status}


				AND e.exam_name LIKE CONCAT(CONCAT('%',#{examName}),'%')


				AND e.exam_start_date = #{examStartDate}


				AND e.exam_end_date <= #{examEndDate}


				AND e.classroom LIKE CONCAT(CONCAT('%',#{classroom}),'%')


				AND e.subject LIKE CONCAT(CONCAT('%',#{subject}),'%')


				AND e.exam_status = #{examStatus}


				AND e.org_id = #{orgId}


				AND e.remark = #{remark}


			ORDER BY  ${sort}

				${sortType}



			LIMIT ${type}

SELECT DISTINCT

from `JY_EXAM_INFO` e
left JOIN `JY_EXAM_STUDENT_RELATION` es
ON e.id = es.exam_id
WHERE 1 = 1

	AND e.exam_id = #{examId}


	AND es.student_id = #{studentId}


	AND es.status = #{stuStatus}


	AND e.status = #{status}


	AND e.remark = #{remark}


	AND e.classroom LIKE CONCAT(CONCAT('%',#{classroom}),'%')


	AND e.subject LIKE CONCAT(CONCAT('%',#{subject}),'%')


	AND e.exam_status = #{examStatus}


	AND e.org_id = #{orgId}


	ORDER BY  ${sort}

		${sortType}

SELECT
DISTINCT

FROM
	JY_EXAM_STUDENT_RELATION es
	LEFT JOIN jy_exam_info e ON es.exam_id = e.id
WHERE 1 = 1

	AND e.exam_id = #{examId}


	AND es.student_id = #{studentId}


	AND es.status = #{stuStatus}


	AND e.status = #{status}


	AND e.remark = #{remark}


	AND e.classroom LIKE CONCAT(CONCAT('%',#{classroom}),'%')


	AND e.subject LIKE CONCAT(CONCAT('%',#{subject}),'%')


	AND e.exam_status = #{examStatus}


	AND e.org_id = #{orgId}


	ORDER BY  ${sort}

		${sortType}



	LIMIT 0,#{type}
